import React,{useRef,useEffect,useContext} from 'react'
import {Container, Row,Button} from 'reactstrap'
import { NavLink,Link ,useNavigate} from 'react-router-dom'

import {AuthContext} from './../../context/AuthContext';

import logo from '../../assets/images/logo.png';
import './header.css';

const nav__links=[
  {
    path:'/home',
    display:'Home'
  },
  {
    path:'/about',
    display:'About'
  },
  {
    path:'/tours',
    display:'Tours'
  },
]

const Header = () => {
  const headerRef=useRef(null);
  const menuRef=useRef(null);
  const navigate = useNavigate()
  const {user,dispatch} = useContext(AuthContext)

  const logout=()=>{
    dispatch({type:'LOGOUT'})
    navigate('/')
  }
  const stickyHeaderFunc=()=>{
    window.addEventListener('scroll',()=>{
      if(document.body.scrollTop>80 || document.documentElement.scrollTop>80){
        headerRef.current.classList.add('sticky__header')
      }else{
        headerRef.current.classList.remove('sticky__header')
      }
    })
  } 
  useEffect(()=>{
    stickyHeaderFunc()

    return window.removeEventListener("scroll",stickyHeaderFunc);
  })

  const toggleMenu = ()=>menuRef.current
  return <header className="header" ref={headerRef}>
     <Container>
     <Row>
      <div className="nav__wrapper d-flex align-itmem-center justify-content-between">
      <div className="logo">
        <img src={logo} alt=""></img>
      </div>
      <div className='navigation'>
        <ul className='menu d-flex align-items-center gap-5'>
          {nav__links.map((item,index)=>(
            <li className='nav__item' key={index}>
              <NavLink to={item.path} className={navClass=> navClass.isActive ? "active__link" : ""}>{item.display}</NavLink>
            </li>
          ))}
        </ul>
      </div>
      <div className='nev__right d-flex align-items-center gap-4'>
       <div className='nev__btns d-flex align-items-center gap-4'>
        {
          user ? (
            <>
              <h5 className='mb-0'>{user.username}</h5>
              <Button className="btn btn-dark" onClick={logout}>
                LOGOUT
              </Button>
            </>
          ):(
            <>
            <Button className="btn secondary__btn">
             <Link to='/login'>Login</Link>
            </Button>
            <Button className='btn primary__btn'><Link to='/register'>Register</Link></Button>
            </>
          )
        }
       </div>
        <span className='mobile__menu'>
        <i class="ri-menu-line"></i>
        </span>
      </div>
      </div>
     </Row>
     </Container>
    </header>
};

export default Header;
